INSERT INTO awareness_campaigns (
    title, title_hi, title_te, title_ta, title_bn, title_mr,
    content, content_hi, content_te, content_ta, content_bn, content_mr,
    disease_id, campaign_type, target_audience, media_type, is_active
) VALUES
(
    'Dengue Prevention Tips', 'डेंगू रोकथाम के उपाय', 'డెంగ్యూ నివారణ చిట్కాలు', 'டெங்கு தடுப்பு குறிப்புகள்', 'ডেঙ্গু প্রতিরোধের টিপস', 'डेंगू प्रतिबंध टिप्स',
    'Remove stagnant water around your home. Use mosquito nets. Wear long sleeves during dawn and dusk.',
    'अपने घर के आसपास ठहरे हुए पानी को हटाएं। मच्छरदानी का उपयोग करें। सुबह और शाम के समय पूरी बाजू के कपड़े पहनें।',
    'మీ ఇంటి చుట్టూ నిలిచిన నీటిని తొలగించండి। దోమల వలలు వాడండి। తెల్లవారుజామున మరియు సాయంత్రం పూర్తి చేతుల దుస్తులు ధరించండి।',
    'உங்கள் வீட்டைச் சுற்றி தேங்கி நிற்கும் நீரை அகற்றவும். கொசு வலைகளைப் பயன்படுத்தவும். விடியல் மற்றும் மாலை நேரங்களில் முழு கை ஆடைகளை அணியவும்।',
    'আপনার বাড়ির চারপাশে জমে থাকা পানি সরান। মশারি ব্যবহার করুন। ভোর ও সন্ধ্যার সময় পূর্ণ হাতার পোশাক পরুন।',
    'तुमच्या घराभोवती साचलेले पाणी काढून टाका। डासदाणी वापरा। पहाट आणि संध्याकाळी पूर्ण बाही कपडे घाला।',
    2, 'prevention', 'general', 'text', true
);
